INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6697, 'Famotidine', 'Pexidartinib', 'Moderate', 'INTERVAL: Coadministration with acid-reducing agents may significantly decrease the bioavailability and therapeutic effects of pexidartinib. The proposed mechanism is a pH-dependent reduction in drug dissolution and/or absorption.', 'DDInter', 'If acid-suppression therapy is required, pexidartinib should be administered at least 2 hours before or 10 hours after taking an H2-receptor antagonist or 2 hours before or 2 hours after taking a locally-acting antacid. Concomitant use of pexidartinib with proton pump inhibitors should be avoided.', 'Absorption', 'If acid-suppression therapy is required, pexidartinib should be administered at least 2 hours before or 10 hours after taking an H2-receptor antagonist or 2 hours before or 2 hours after taking a locally-acting antacid.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6701/', '', 'Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Amoxicillin', 'Tepotinib, Larotrectinib, Avapritinib, Fedratinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Duvelisib, Tivozanib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6698, 'Famotidine', 'Phenytoin', 'Moderate', 'Coadministration with famotidine or ranitidine may rarely increase the plasma concentrations of phenytoin, resulting in toxicity. The mechanism of interaction is unknown.', 'DDInter', 'Until more information is available, caution is advised if phenytoin is prescribed in combination with famotidine or ranitidine, particularly in elderly patients. Clinicians should be alert for signs and symptoms of phenytoin toxicity such as ataxia, incoordination, tremor, nystagmus, hypotension, slurred speech, lethargy, nausea, vomiting, mental confusion, and psychosis. The possibility of an interaction should be considered if toxicity occurs shortly (e.g., within a month) after initiation or change of dosage of the H2-receptor antagonist. Both phenytoin and the H2-receptor antagonist may need to be withdrawn until the patient recovers.', 'Others', 'Until more information is available, caution is advised if phenytoin is prescribed in combination with famotidine or ranitidine, particularly in elderly patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6702/', '', 'Brivaracetam, Cenobamate, Methylphenobarbital, Ethosuximide, Lacosamide, Ethotoin, Rufinamide, Paramethadione, Perampanel, Ganaxolone, Vigabatrin, More', 'Tetracycline, Dexlansoprazole, Bismuth subcitrate potassium, Nizatidine, Levofloxacin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6699, 'Famotidine', 'Pimavanserin', 'Moderate', 'Famotidine may cause QTc prolongation. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution and clinical monitoring are recommended if famotidine is used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended if famotidine is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6703/', '', 'Tinidazole, Pantoprazole, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Nizatidine, Esomeprazole, Ranitidine, More', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 1767369485);
