INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6797, 'Cyanocobalamin', 'Ranitidine', 'Minor', 'By reducing or suppressing gastric acid secretion, H2-receptor antagonists and proton pump inhibitors may interfere with the gastrointestinal absorption of vitamin B12, a process that is dependent on the presence of gastric acid and pepsin. Clinical studies have shown that dietary (i.e., protein-bound) vitamin B12 malabsorption can occur during treatment with these agents, particularly proton pump inhibitors, although the likelihood of developing clinically significant deficiency over time is unknown.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6801/', '', 'Iron protein succinylate, Iron, Ferrous gluconate, Ferric maltol, Iron sucrose, Ferrous fumarate', 'Tinidazole, Amoxicillin, Tetracycline, Bismuth subcitrate potassium, Rifabutin, Vonoprazan, Levofloxacin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6798, 'Cyclosporine', 'Ranitidine', 'Minor', 'Increases in serum creatinine have been reported during concomitant use of ranitidine and cyclosporine by kidney transplant patients. Data have been conflicting; no changes in creatinine have also been reported. The mechanism and clinical significance are unknown.', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6802/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Abatacept, Upadacitinib, More', 'Vonoprazan, Bismuth subcitrate potassium, Levofloxacin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6799, 'Dabrafenib', 'Ranitidine', 'Moderate', 'Drugs that alter the pH of the upper gastrointestinal tract may affect the solubility of dabrafenib and reduce its bioavailability. The interaction has not been formally studied with H2-receptor antagonists or antacids; however, the solubility of dabrafenib is known to be pH-dependent. It has been reported to be very slightly soluble at pH 1 and practically insoluble above pH 4 in aqueous media.', 'DDInter', 'Until more information is available, caution is advised if dabrafenib is used in combination with H2-receptor antagonists or antacids. The potential for diminished therapeutic effects of dabrafenib should be considered, and pharmacologic response should be closely monitored.', 'Absorption', 'Until more information is available, caution is advised if dabrafenib is used in combination with H2-receptor antagonists or antacids.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6803/', '', 'Pantoprazole, Levofloxacin, Tetracycline, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Esomeprazole, Amoxicillin, More', 'Entrectinib, Pralsetinib, Asciminib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Vemurafenib, Selumetinib, Ribociclib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6800, 'Dacomitinib', 'Ranitidine', 'Moderate', 'INTERVAL: Coadministration with proton pump inhibitors (PPIs) may decrease the plasma concentrations of dacomitinib. The proposed mechanism is a pH-dependent reduction in dissolution or absorption of dacomitinib due to prolonged gastric acid suppression induced by PPIs.', 'DDInter', 'The manufacturer recommends taking dacomitinib at least 6 hours before or 10 hours after H2-receptor antagonists. As an alternative, locally-acting antacids may be considered. Concomitant use of dacomitinib with PPIs should generally be avoided.', 'Absorption', 'The manufacturer recommends taking dacomitinib at least 6 hours before or 10 hours after H2-receptor antagonists.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6804/', '', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Rifabutin, Clarithromycin, Vonoprazan, Amoxicillin', 'Entrectinib, Pralsetinib, Asciminib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Vemurafenib, Selumetinib, Ribociclib, More', 1767369485);
