INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6804, 'Dicoumarol', 'Ranitidine', 'Moderate', 'Limited data suggest that ranitidine may occasionally alter the hypoprothrombinemic effect of coumarin anticoagulants. There have been rare reports of altered prothrombin time or INR (both increases and decreases) during coadministration of ranitidine and warfarin. The mechanism of interaction is unknown. Ranitidine at usual therapeutic dosages has not been shown to significantly affect CYP450-mediated metabolism.', 'DDInter', 'Until more data are available, caution may be advisable if ranitidine is used in combination with coumarin anticoagulants. The INR should be checked regularly and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of ranitidine in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', 'Until more data are available, caution may be advisable if ranitidine is used in combination with coumarin anticoagulants.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6808/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Urokinase, Cangrelor, Caplacizumab, Argatroban, Desirudin, More', 'Misoprostol, Bismuth subcitrate potassium, Famotidine, Nizatidine, Vonoprazan', 1767369485);
