INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6912, 'Rabeprazole', 'Armodafinil', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 2C19 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2C19 activity by modafinil and armodafinil.', 'DDInter', 'Caution is advised if modafinil or armodafinil must be used concurrently with medications that undergo metabolism by CYP450 2C19, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever modafinil or armodafinil is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if modafinil or armodafinil must be used concurrently with medications that undergo metabolism by CYP450 2C19, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6916/', '', 'Tinidazole, Levofloxacin, Tetracycline, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, Nizatidine, Ranitidine, More', 'Pemoline, Metamfetamine, Lisdexamfetamine, Solriamfetol, Caffeine, Dexmethylphenidate, Dextroamphetamine, Citicoline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6913, 'Acetylsalicylic acid', 'Rabeprazole', 'Minor', 'Coadministration with proton pump inhibitors may decrease the oral bioavailability of aspirin and other salicylates. Proton pump inhibitors may enhance the release rate of salicylates from enteric-coated formulations due to premature disruption of the coating and intragastric release of the drug secondary to an increase in gastric pH. The clinical significance of this interaction is unknown. Theoretically, it may increase the risk of gastric adverse effects associated with salicylates.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6917/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, Argatroban, More', 'Tinidazole, Bismuth subcitrate potassium, Rifabutin, Vonoprazan, Chondroitin sulfate, Glucosamine, Tolazoline, Dimethyl sulfoxide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (6914, 'Rabeprazole', 'Atazanavir', 'Major', 'Concurrent use of proton pump inhibitors may decrease the oral bioavailability of atazanavir and substantially reduce its concentrations in plasma. Atazanavir solubility decreases with increasing pH, thus inhibition of gastric acid secretion may interfere with dissolution of the drug.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, use of proton pump inhibitors is not recommended in treatment-experienced patients receiving atazanavir with or without ritonavir or cobicistat. In treatment-naive patients receiving atazanavir with ritonavir or cobicistat, it is recommended that the dose of the proton-pump inhibitor not exceed 20 mg/day of omeprazole, or equivalent, and doses should be separated by 12 hours. Atazanavir without ritonavir or cobicistat should not be coadministered with proton pump inhibitors in treatment-naive patients.', 'Absorption', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, use of proton pump inhibitors is not recommended in treatment-experienced patients receiving atazanavir with or without ritonavir or cobicistat.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6918/', '', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Telaprevir, Dolutegravir, Oseltamivir, More', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Amoxicillin, Ketoprofen, Diclofenac, Piroxicam, Rofecoxib, More', 1767369485);
