INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (7001, 'Rabeprazole', 'Posaconazole', 'Moderate', 'Coadministration with proton pump inhibitors (PPIs) may decrease the systemic bioavailability of posaconazole from the oral suspension. The proposed mechanism is reduced solubility and absorption of posaconazole due to an increase in gastric pH induced by these agents.', 'DDInter', 'Concomitant use of posaconazole oral suspension with proton pump inhibitors should generally be avoided. If possible, prescribers may consider suspending the PPI until completion of posaconazole therapy, or substituting an antifungal agent like fluconazole or voriconazole whose absorption is not affected by stomach pH. Delayed-release tablets of posaconazole may be used with PPIs, as no pharmacokinetic interaction has been demonstrated with esomeprazole.', 'Absorption', 'Concomitant use of posaconazole oral suspension with proton pump inhibitors should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7005/', '', 'Tinidazole, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Vonoprazan, Amoxicillin, Ketoprofen, Diclofenac, Piroxicam, Rofecoxib, More', 'Micafungin, Caspofungin, Miconazole, Oteseconazole, Flucytosine, Anidulafungin', 1767369485);
