INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (7201, 'Formoterol', 'Astemizole', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7205/', '', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Acrivastine, Triprolidine, Tripelennamine, Dexbrompheniramine, Clemastine, Doxylamine, Meclizine, More', 'Mometasone furoate, Umeclidinium, Budesonide, Glycopyrronium, Aclidinium, Mometasone, Epinephrine, Fluticasone, Cromoglicic acid, Ipratropium, Bitolterol, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (7202, 'Formoterol', 'Atenolol', 'Moderate', 'Although cardioselective beta-blockers do not generally inhibit the bronchodilating effect of beta-2 adrenergic agonists, they may worsen pulmonary function in patients with asthma or other obstructive airway diseases. Cardioselective beta-blockers can occasionally precipitate acute bronchospasm in these patients, despite relative selectivity for beta-1 receptors in cardiac tissues. Presumably, beta-1 selectivity is dose-dependent and may be lost given a sufficient dose of the beta-blocker in susceptible patients. Other mechanisms may also be involved in bronchoconstriction that are unrelated to beta-2 blockade--for example, their effects on cholinergic M2 receptors and alpha-1 adrenoreceptors.', 'DDInter', 'Beta-blockers, including those with relative cardioselectivity, should generally be avoided in patients with bronchospastic diseases. However, given their demonstrated benefit in such conditions as heart failure, myocardial infarction, cardiac arrhythmias and hypertension, cardioselective beta-blockers may be administered with caution to those who do not respond to or tolerate alternative treatment. The benefits generally outweigh the risks in patients with mild or moderate reactive airway disease that is well controlled on inhaled corticosteroids and beta-2 agonists, provided they have no prior history suggesting a predisposition to severe exacerbations.', 'Others', 'Beta-blockers, including those with relative cardioselectivity, should generally be avoided in patients with bronchospastic diseases.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7206/', '', 'Nifedipine', 'Mometasone furoate, Umeclidinium, Aclidinium, Mometasone, Cromoglicic acid, Ipratropium, Beclomethasone dipropionate', 1767369485);
