INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (7741, 'Racepinephrine', 'Pseudoephedrine', 'Moderate', 'Coadministration of beta-2 adrenergic agonists with other adrenergic agents may potentiate the risk of cardiovascular side effects. Beta-2 adrenergic agonists can produce clinically significant cardiovascular effects including increases in pulse rate and systolic or diastolic blood pressure as well as ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression. The risk is lower when beta-2 adrenergic agonists are inhaled at normally recommended dosages.', 'DDInter', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension. Blood pressure and heart rate should be closely monitored.', 'Synergism', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7745/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (7742, 'Rasagiline', 'Pseudoephedrine', 'Moderate', 'Coadministration of nonselective monoamine oxidase (MAO) inhibitors and sympathomimetic amines has been associated with severe hypertensive reactions. The mechanism involves a synergistic sympathomimetic effect due to enhanced norepinephrine storage in adrenergic neurons (MAOI activity) and increased liberation or decreased reuptake of catecholamines (sympathomimetic activity).', 'DDInter', 'Caution is advised if rasagiline is used in combination with sympathomimetic amines. Patients with preexisting hypertension may require more frequent monitoring of their blood pressure.', 'Synergism', 'Caution is advised if rasagiline is used in combination with sympathomimetic amines.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7746/', '', 'Apomorphine, Ropinirole, Opicapone, Cabergoline, Entacapone, Tolcapone, Rotigotine, Pergolide, Pramipexole, Amantadine', '', 1767369485);
