INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8036, 'Cyclosporine', 'Ketorolac', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the nephrotoxic effects of cyclosporine, especially if dehydration is present. The exact mechanism is unknown but is apparently unrelated to plasma cyclosporine levels. The interaction has been reported with diclofenac and sulindac.', 'DDInter', 'Renal function should be closely monitored in patients receiving concomitant therapy with cyclosporine and NSAIDs.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8040/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Chondroitin sulfate, Glucosamine, Homatropine, Methscopolamine, Ephedrine, Tropicamide, Phenylephrine, Fluocinolone acetonide, Bromfenac', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8037, 'Human cytomegalovirus immune globulin', 'Ketorolac', 'Major', 'Coadministration of intravenous immune globulin preparations with nephrotoxic agents may potentiate the risk of renal impairment. Many commercially available intravenous formulations of immune globulin contain sucrose as a stabilizer.', 'DDInter', 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.g., e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Clinicians should ensure that patients are not volume depleted prior to the initiation of immune globulin therapy. Monitoring of urine output and renal function tests, including the measurement of blood urea nitrogen (BUN) and serum creatinine, is recommended prior to the initial infusion and at appropriate intervals thereafter.', 'Synergism', 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8041/', '', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Homatropine, Atropine, Scopolamine, Methscopolamine, Ephedrine, Tropicamide, More', 'Bezlotoxumab, Palivizumab', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8038, 'Dalteparin', 'Ketorolac', 'Major', 'In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during low molecular weight heparin (LMWH) or heparinoid therapy may be increased by the concomitant use of other drugs that affect coagulation, including nonsteroidal anti-inflammatory drugs (NSAIDs). The development of epidural and spinal hematoma can lead to long-term or permanent paralysis. Theoretically, NSAIDs may potentiate the risk of bleeding complications associated with LMWH or heparinoid therapy. NSAIDs interfere with platelet adhesion and aggregation and may prolong bleeding time in healthy individuals. While these effects are generally slight and of relatively short duration with most NSAIDs (except aspirin) at recommended dosages, they may be of pronounced clinical significance when combined with the inhibitory effects of LMWHs or heparinoids on the clotting cascade.', 'DDInter', 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits.', 'Synergism', 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8042/', '', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Homatropine, Atropine, Scopolamine, Methscopolamine, Ephedrine, Tropicamide, More', 'Protein C, Acenocoumarol, Selexipag', 1767369485);
