INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8176, 'Omacetaxine mepesuccinate', 'Ketorolac', 'Major', 'Coadministration of omacetaxine and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with omacetaxine is associated with a high frequency of thrombocytopenia.', 'DDInter', 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The CBC and platelet count should be monitored according to the manufacturer''s recommendations.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8180/', '', 'Rabeprazole, Misoprostol, Glucosamine, Chondroitin sulfate, Rofecoxib, Esomeprazole, Celecoxib, Valdecoxib, Homatropine, Atropine, Scopolamine, More', 'Pertuzumab, Olaparib, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, Enasidenib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8177, 'Omega-3 fatty acids', 'Ketorolac', 'Moderate', 'Omega-3 fatty acids (e.g., fish oil) may potentiate the pharmacologic effects of anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, dextran, and nonsteroidal anti-inflammatory drugs (NSAIDs). The exact mechanism of interaction is unknown. Omega-3 fatty acids may possess mild antiplatelet and hypocoagulant activities.', 'DDInter', 'In general, patients should consult a healthcare provider before taking any herbal or nutritional supplements. Patients using omega-3 fatty acids in combination with anticoagulants or other drugs that affect hemostasis should be advised of the potential for increased risk of bleeding complications.', 'Synergism', 'In general, patients should consult a healthcare provider before taking any herbal or nutritional supplements.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8181/', '', 'Rabeprazole, Misoprostol, Glucosamine, Chondroitin sulfate, Rofecoxib, Esomeprazole, Celecoxib, Valdecoxib, Homatropine, Atropine, Scopolamine, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8178, 'Oxaliplatin', 'Ketorolac', 'Moderate', 'Theoretically, coadministration with drugs that are nephrotoxic may delay and/or decrease the clearance of oxaliplatin, which is primarily eliminated unchanged by the kidney.', 'DDInter', 'Caution is advised if oxaliplatin is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). The potential for increased toxicity of oxaliplatin such as peripheral sensory neuropathies and neutropenia should be considered. Renal function should be closely monitored during therapy.', 'Excretion', 'Caution is advised if oxaliplatin is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8182/', '', 'Chondroitin sulfate, Esomeprazole, Glucosamine, Homatropine, Methscopolamine, Ephedrine, Scopolamine, Tropicamide, Phenylephrine, Salicylic acid, Fluocinolone acetonide, More', 'Pertuzumab, Olaparib, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, Enasidenib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8179, 'Oxaprozin', 'Ketorolac', 'Major', 'The use of ketorolac in combination with other nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the risk of serious adverse effects such as renal failure and gastrointestinal toxicity including inflammation, bleeding, ulceration, and perforation of the esophagus, stomach, or intestines.', 'DDInter', 'Concurrent use of ketorolac with other NSAIDs or aspirin is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8183/', '', 'Chondroitin sulfate, Esomeprazole, Glucosamine, Oxaprozin, Homatropine, Atropine, Scopolamine, Methscopolamine, Ephedrine, Tropicamide, Phenylephrine, More', 'Glucosamine, Chondroitin sulfate, Esomeprazole, Ketorolac', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8180, 'Pamidronic acid', 'Ketorolac', 'Moderate', 'Theoretical concerns exist regarding the potential for increased risk and severity of gastrointestinal toxicity during coadministration of oral bisphosphonates and nonsteroidal anti-inflammatory drugs (NSAIDs) due to additive or synergistic irritant effects on the gastrointestinal mucosa. Because NSAIDs reduce the rate of ulcer healing in the stomach and duodenum, it is also possible that NSAIDs may delay healing and exaggerate the mucosal injury caused by oral bisphosphonates. Theoretical concerns exist regarding the potential for increased risk and severity of renal impairment during coadministration of bisphosphonates with high dosages or chronic use of NSAIDs due to additive or synergistic nephrotoxic effects on the kidney. The use of bisphosphonates has been associated with nephrotoxicity manifested as deterioration of renal function and renal failure. Cases have primarily involved intravenous formulations of the drugs such as pamidronic acid and zoledronic acid, especially when they are administered too rapidly. The risk of hypocalcemia may also be increased, as drug-induced renal tubular damage can lead to renal loss of calcium and other electrolytes such as magnesium. Bisphosphonates alone often cause mild, asymptomatic hypocalcemia via inhibitive effects on bone resorption and possibly chelation of blood calcium. Chronic use of nonsteroidal anti-inflammatory drugs (NSAIDs) may be associated with renal toxicities including elevations in serum creatinine and BUN, tubular necrosis, glomerulitis, renal papillary necrosis, acute interstitial nephritis, nephrotic syndrome, and renal failure.', 'DDInter', 'Caution is advised if bisphosphonates are prescribed in combination with NSAIDs. Patients receiving oral bisphosphonates should be closely monitored for the development of gastrointestinal toxicity and advised to immediately report potential signs and symptoms such as severe abdominal pain, nausea, vomiting, diarrhea, loss of appetite, dizziness, lightheadedness, and/or black, tarry stools. Patients receiving intravenous formulations of bisphosphonates should have renal function and serum electrolytes closely monitored.', 'Synergism', 'Caution is advised if bisphosphonates are prescribed in combination with NSAIDs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8184/', '', 'Vosoritide, Denosumab, Cholecalciferol, Burosumab, Romosozumab', 'Misoprostol, Chondroitin sulfate, Esomeprazole, Glucosamine, Homatropine, Atropine, Scopolamine, Methscopolamine, Ephedrine, Tropicamide, Phenylephrine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8181, 'Panobinostat', 'Ketorolac', 'Major', 'Coadministration of panobinostat and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with panobinostat has been associated with severe thrombocytopenia and hemorrhage (including gastrointestinal and pulmonary hemorrhage) with fatal outcomes. The risk may be increased in patients with coagulation disorders or those on chronic anticoagulation therapy.', 'DDInter', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with panobinostat. Close clinical and laboratory observation for bleeding complications is recommended during therapy. A complete blood cell count should be performed prior to and at least weekly during treatment according to treatment protocols, including monitoring for thrombocytopenia. Dose modifications may be required based on individual patient tolerability. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with panobinostat.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8185/', '', 'Rabeprazole, Misoprostol, Glucosamine, Chondroitin sulfate, Rofecoxib, Esomeprazole, Celecoxib, Valdecoxib, Homatropine, Atropine, Scopolamine, More', 'Pertuzumab, Olaparib, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, Enasidenib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8182, 'Paroxetine', 'Ketorolac', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8186/', '', 'Chondroitin sulfate, Esomeprazole, Glucosamine, Homatropine, Methscopolamine, Ephedrine, Tropicamide, Phenylephrine, Fluocinolone acetonide, Cortisone, Betamethasone, Fluorometholone', 'Phenelzine, Desipramine, Tranylcypromine, Trimipramine, Maprotiline, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Tryptophan, Amoxapine, More', 1767369485);
