INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8280, 'Warfarin', 'Ketorolac', 'Major', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the hypoprothrombinemic effect and bleeding risk associated with oral anticoagulants.', 'DDInter', 'NSAIDs should be administered with oral anticoagulants only if benefit outweighs risk. The INR should be checked frequently and oral anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of NSAIDs in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools. Salicylates (except aspirin) appear to have less effect on coagulation and may be preferable in patients treated with oral anticoagulants.', 'Synergism', 'NSAIDs should be administered with oral anticoagulants only if benefit outweighs risk.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8284/', '', 'Homatropine, Methscopolamine, Ephedrine, Scopolamine, Phenylephrine, Fluocinolone acetonide, Fluorometholone', 'Protein C, Acenocoumarol, Selexipag', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8281, 'Zanubrutinib', 'Ketorolac', 'Major', 'Coadministration of zanubrutinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 'Concomitant use of zanubrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution. Close clinical and laboratory monitoring for bleeding complications is recommended during therapy. Patients should be advised to promptly report any signs and symptoms of bleeding to their physician. Discontinue zanubrutinib if intracranial hemorrhage of any grade occurs.', 'Synergism', 'Concomitant use of zanubrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8285/', '', 'Rabeprazole, Misoprostol, Glucosamine, Chondroitin sulfate, Rofecoxib, Esomeprazole, Celecoxib, Valdecoxib, Homatropine, Atropine, Scopolamine, More', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Larotrectinib, Bosutinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8282, 'Zoledronic acid', 'Ketorolac', 'Moderate', 'Theoretical concerns exist regarding the potential for increased risk and severity of gastrointestinal toxicity during coadministration of oral bisphosphonates and nonsteroidal anti-inflammatory drugs (NSAIDs) due to additive or synergistic irritant effects on the gastrointestinal mucosa. Because NSAIDs reduce the rate of ulcer healing in the stomach and duodenum, it is also possible that NSAIDs may delay healing and exaggerate the mucosal injury caused by oral bisphosphonates. Theoretical concerns exist regarding the potential for increased risk and severity of renal impairment during coadministration of bisphosphonates with high dosages or chronic use of NSAIDs due to additive or synergistic nephrotoxic effects on the kidney. The use of bisphosphonates has been associated with nephrotoxicity manifested as deterioration of renal function and renal failure. Cases have primarily involved intravenous formulations of the drugs such as pamidronic acid and zoledronic acid, especially when they are administered too rapidly. The risk of hypocalcemia may also be increased, as drug-induced renal tubular damage can lead to renal loss of calcium and other electrolytes such as magnesium. Bisphosphonates alone often cause mild, asymptomatic hypocalcemia via inhibitive effects on bone resorption and possibly chelation of blood calcium. Chronic use of nonsteroidal anti-inflammatory drugs (NSAIDs) may be associated with renal toxicities including elevations in serum creatinine and BUN, tubular necrosis, glomerulitis, renal papillary necrosis, acute interstitial nephritis, nephrotic syndrome, and renal failure.', 'DDInter', 'Caution is advised if bisphosphonates are prescribed in combination with NSAIDs. Patients receiving oral bisphosphonates should be closely monitored for the development of gastrointestinal toxicity and advised to immediately report potential signs and symptoms such as severe abdominal pain, nausea, vomiting, diarrhea, loss of appetite, dizziness, lightheadedness, and/or black, tarry stools. Patients receiving intravenous formulations of bisphosphonates should have renal function and serum electrolytes closely monitored.', 'Synergism', 'Caution is advised if bisphosphonates are prescribed in combination with NSAIDs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8286/', '', 'Vosoritide, Denosumab, Cholecalciferol, Burosumab, Romosozumab', 'Misoprostol, Chondroitin sulfate, Esomeprazole, Glucosamine, Homatropine, Methscopolamine, Ephedrine, Scopolamine, Tropicamide, Phenylephrine, Fluocinolone acetonide, More', 1767369485);
