INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8384, 'Acyclovir', 'Plazomicin', 'Moderate', 'The concurrent or sequential use of two or more potentially nephrotoxic or neurotoxic drugs may increase the risk of toxicity. Patients who are elderly, debilitated, dehydrated, or who have preexisting renal dysfunction may have a higher risk of developing adverse reactions.', 'DDInter', 'Close clinical and laboratory monitoring of renal and neurologic function and drug serum concentrations (if clinically appropriate) is recommended.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8388/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, More', 'Netilmicin, Streptomycin, Kanamycin', 1767369485);
