INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8396, 'Acyclovir', 'Varicella Zoster Vaccine (Recombinant)', 'Major', 'Concomitant use of live, attenuated varicella virus vaccine or zoster vaccine with acyclovir, famciclovir, or valacyclovir may result in diminished immunologic response to the vaccine. Although clinical data are lacking, theoretical concern exists that antiviral activity against the vaccine strain may prevent a sufficient degree of replication to occur in order to induce a protective immune response. Acyclovir, specifically, has demonstrated activity against the Oka strain of live, attenuated varicella-zoster virus used in the vaccines.', 'DDInter', 'Acyclovir, famciclovir, and valacyclovir should not be used for at least 24 hours before administration of live, attenuated varicella virus vaccine or zoster vaccine. In addition, these antiviral agents should generally be avoided for 14 days after vaccination.', 'Antagonism', 'Acyclovir, famciclovir, and valacyclovir should not be used for at least 24 hours before administration of live, attenuated varicella virus vaccine or zoster vaccine.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8400/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', '', 1767369485);
