INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8607, 'Amifostine', 'Nifedipine', 'Moderate', 'Concomitant use with hypotensive agents may increase the risk and severity of hypotension associated with amifostine. Hypotension has been reported frequently in patients treated with amifostine. It is usually transient (lasting from 5 to 15 minutes) and closely associated with amifostine administration, but may persist or become evident hours after administration.', 'DDInter', 'If medically feasible, antihypertensive medications should be interrupted 24 hours prior to amifostine administration. Blood pressure should be closely monitored during and after treatment. In addition to hypotension, transient hypertension or exacerbation of preexisting hypertension may occur from intravenous hydration, discontinuation of antihypertensive medications, or other causes. Patients who continue their hypotensive medication(s) during amifostine therapy should be carefully monitored for the development of hypotension.', 'Synergism', 'If medically feasible, antihypertensive medications should be interrupted 24 hours prior to amifostine administration.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8611/', '', 'Celecoxib, Ivabradine', 'Hydroxocobalamin, Fomepizole, Ipecac, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Deferoxamine, Levoleucovorin, Arginine, Methionine, More', 1767369485);
