INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8620, 'Nifedipine', 'Armodafinil', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Modafinil and armodafinil are modest inducers of CYP450 3A4, and pharmacokinetic studies suggest that their effects may be primarily intestinal rather than hepatic. Thus, clinically significant interactions would most likely be expected with drugs that have low oral bioavailability due to significant intestinal CYP450 3A4-mediated first-pass metabolism (e.g., buspirone, cyclosporine, lovastatin, midazolam, saquinavir, simvastatin, sirolimus, tacrolimus, triazolam, calcium channel blockers).', 'DDInter', 'Pharmacologic response to these drugs may be altered and should be monitored more closely whenever modafinil or armodafinil is added to or withdrawn from therapy. Dosage adjustments may be required if an interaction is suspected.', 'Metabolism', 'Pharmacologic response to these drugs may be altered and should be monitored more closely whenever modafinil or armodafinil is added to or withdrawn from therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8624/', '', 'Clevidipine, Celecoxib, Metoprolol, Carvedilol, Nebivolol, Bisoprolol, Sotalol, Acetylsalicylic acid, Atenolol', 'Amphetamine, Pemoline, Metamfetamine, Lisdexamfetamine, Solriamfetol, Caffeine, Dextroamphetamine, Citicoline', 1767369485);
