INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8685, 'Cisapride', 'Nifedipine', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cisapride, which is primarily metabolized by the isoenzyme. High plasma levels of cisapride have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia, ventricular fibrillation, and torsade de pointes; cardiac arrest; and sudden death.', 'DDInter', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, the concomitant use with potent CYP450 3A4 inhibitors (e.g., azole antifungal agents, protease inhibitors, certain macrolide antibiotics, nefazodone) is considered contraindicated. Use with other agents known to inhibit CYP450 3A4 should generally be avoided if possible.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, the concomitant use with potent CYP450 3A4 inhibitors (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8689/', '', 'Felodipine, Amlodipine, Nisoldipine, Nicardipine, Isradipine, Clevidipine, Levamlodipine, Celecoxib, Nimodipine, Amlodipine, Felodipine, More', '', 1767369485);
