INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8850, 'Nifedipine', 'Lonafarnib', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lonafarnib. In vitro, lonafarnib has been shown to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 1A2, 2A6, 2C8, 2C9, 2C19, and 2E1.', 'DDInter', 'Concomitant use of lonafarnib with weak CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing to or continuing lonafarnib at the starting dosage of 115 mg/m2 twice daily (for patients 12 months of age or older with a body surface area of at least 0.39 m2). Patients should be closely monitored for common adverse effects as well as arrhythmias and events such as syncope and heart palpitations, since lonafarnib exposures may be increased despite the dosage reduction and the effect on the QT interval is unknown. The previous dosage of lonafarnib may be resumed 14 days after discontinuing the weak CYP450 3A inhibitor.', 'Metabolism', 'Concomitant use of lonafarnib with weak CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8854/', '', 'Clevidipine, Celecoxib, Levamlodipine, Metoprolol, Carvedilol, Nebivolol, Bisoprolol, Sotalol, Acetylsalicylic acid, Atenolol', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 1767369485);
