INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8998, 'Selegiline', 'Nifedipine', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the hypotensive effect of some medications. MAOIs alone quite commonly produce orthostatic hypotension. This effect may stem from a gradual MAOI-induced accumulation of false neurotransmitters in peripheral adrenergic neurons that have minimal activity at alpha- and beta-adrenergic receptors, resulting in a functional block of sympathetic neurotransmission. The interaction has been reported with the concomitant use of beta-blockers.', 'DDInter', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment. Close monitoring for development of hypotension is recommended. Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9002/', '', 'Opicapone, Rotigotine, Tolcapone, Pergolide, Amantadine', 'Mibefradil, Celecoxib, Ivabradine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (8999, 'Selexipag', 'Nifedipine', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', 'DDInter', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9003/', '', 'Celecoxib, Acetylsalicylic acid, Ivabradine', 'Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, Streptokinase, Tinzaparin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (9000, 'Selpercatinib', 'Nifedipine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of selpercatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised when selpercatinib is used concomitantly with CYP450 3A4 inhibitors. Patients should be closely monitored for development of serious adverse effects such as QT interval prolongation, liver transaminase and bilirubin elevations, hypertension, hemorrhage, edema and hypersensitivity reactions (e.g., fever, rash, arthralgias/myalgias with concurrent decreased platelets or transaminitis), and the dosing of selpercatinib adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when selpercatinib is used concomitantly with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9004/', '', 'Clevidipine, Celecoxib, Levamlodipine, Propranolol, Metoprolol, Carvedilol, Nebivolol, Bisoprolol, Acetylsalicylic acid, Atenolol', 'Dacomitinib, Tepotinib, Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, More', 1767369485);
