INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (9077, 'Voxelotor', 'Nifedipine', 'Moderate', 'Coadministration with voxelotor may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 3A4 by voxelotor. The interaction may be significant for sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index.', 'DDInter', 'Caution is advised when voxelotor is used concomitantly with drugs that undergo metabolism by CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever voxelotor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when voxelotor is used concomitantly with drugs that undergo metabolism by CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9081/', '', 'Clevidipine, Celecoxib, Propranolol, Metoprolol, Carvedilol, Nebivolol, Bisoprolol, Sotalol, Acetylsalicylic acid, Atenolol', 'Hemin, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Crizanlizumab, Lanadelumab, Icatibant', 1767369485);
