INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (9421, 'Aminolevulinic acid (topical)', 'Adapalene (topical)', 'Moderate', 'Concomitant use of aminolevulinate topical preparations with other known photosensitizing agents may enhance the phototoxic reaction to photodynamic therapy. Medicinal products with known phototoxic or photoallergic potential include fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, and hypericin extracts (e.g., St John''s Wort).', 'DDInter', 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid exposure of treated areas to sunlight or bright indoor lights (e.g., examination lamps, operating room lamps, tanning beds, lights at close proximity) during the period between application of aminolevulinic acid or methyl aminolevulinate and photoactivation, and for 48 hours post-illumination. Concomitant use with other topical medicinal products should be avoided.', 'Synergism', 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9425/', '', 'Clindamycin, Vitamin A, Dexamethasone, Chloramphenicol, Fluorometholone, Erythromycin, Methylprednisolone, Azelaic acid, Adapalene', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 1767369485);
