INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (9895, 'Trastuzumab emtansine', 'Ponatinib', 'Major', 'Coadministration of ponatinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with ponatinib is associated with severe, sometimes fatal hemorrhage including cerebral and gastrointestinal hemorrhage.', 'DDInter', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with ponatinib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The INR should be monitored more frequently during coadministration of warfarin. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with ponatinib.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9899/', '', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Amivantamab, Atezolizumab, Pembrolizumab, More', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Dacomitinib, Tepotinib, Fedratinib, Bosutinib, Vemurafenib, Lapatinib, Selumetinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (9896, 'Trastuzumab emtansine', 'Posaconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase exposure to the cytotoxic component of ado-trastuzumab emtansine known as DM1, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 3A5.', 'DDInter', 'The use of ado-trastuzumab emtansine in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided. Some authorities recommend avoiding concomitant use of ado-trastuzumab emtansine during and for 2 weeks after treatment with itraconazole. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist, consider delaying initiation of ado-trastuzumab emtansine until therapy with the potent CYP450 3A4 inhibitor is complete and the drug has cleared from the circulation, or approximately 3 elimination half-lives. When concomitant administration is necessary, patients should be closely monitored for adverse reactions such as hepatic impairment, left ventricular dysfunction, peripheral neuropathy, and thrombocytopenia.', 'Metabolism', 'The use of ado-trastuzumab emtansine in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9900/', '', 'Fluconazole, Caspofungin, Micafungin, Amphotericin B, Miconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (9897, 'Trastuzumab emtansine', 'Prasugrel', 'Major', 'Coadministration of trastuzumab emtansine with anticoagulant or antiplatelet drugs may potentiate the risk of hemorrhagic events associated with these drugs. In clinical trials, fatal cases of hemorrhage occurred in patients receiving trastuzumab emtansine in addition to anticoagulant and antiplatelet therapy.', 'DDInter', 'Caution is recommended and additional monitoring should be considered if trastuzumab emtansine must be used with anticoagulant or antiplatelet drugs. In general, monitoring of platelet counts is recommended prior to each dose of trastuzumab emtansine.', 'Synergism', 'Caution is recommended and additional monitoring should be considered if trastuzumab emtansine must be used with anticoagulant or antiplatelet drugs.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9901/', '', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Amivantamab, Atezolizumab, Pembrolizumab, More', 'Alteplase, Streptokinase, Defibrotide, Antithrombin III human, Tenecteplase, Protein C, Acenocoumarol, Selexipag, Urokinase, Anistreplase, Reteplase', 1767369485);
