INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (9901, 'Trastuzumab emtansine', 'Remdesivir', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', 'DDInter', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9905/', '', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (9902, 'Trastuzumab emtansine', 'Ritonavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase exposure to the cytotoxic component of ado-trastuzumab emtansine known as DM1, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 3A5.', 'DDInter', 'The use of ado-trastuzumab emtansine in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided. Some authorities recommend avoiding concomitant use of ado-trastuzumab emtansine during and for 2 weeks after treatment with itraconazole. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist, consider delaying initiation of ado-trastuzumab emtansine until therapy with the potent CYP450 3A4 inhibitor is complete and the drug has cleared from the circulation, or approximately 3 elimination half-lives. When concomitant administration is necessary, patients should be closely monitored for adverse reactions such as hepatic impairment, left ventricular dysfunction, peripheral neuropathy, and thrombocytopenia.', 'Metabolism', 'The use of ado-trastuzumab emtansine in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9906/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
