INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (10029, 'Isoetharine', 'Epinephrine', 'Moderate', 'Coadministration of beta-2 adrenergic agonists with other adrenergic agents may potentiate the risk of cardiovascular side effects. Beta-2 adrenergic agonists can produce clinically significant cardiovascular effects including increases in pulse rate and systolic or diastolic blood pressure as well as ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression. The risk is lower when beta-2 adrenergic agonists are inhaled at normally recommended dosages.', 'DDInter', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension. Blood pressure and heart rate should be closely monitored.', 'Synergism', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10033/', '', 'Tetracycline, Minocycline, Amphotericin B, Miconazole, Doxycycline, Metronidazole, Clotrimazole, Acetylsalicylic acid, Chlorhexidine, Neomycin, Mometasone furoate, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (10030, 'Isoflurane', 'Epinephrine', 'Major', 'Rarely, the combination of epinephrine and some anesthetics has resulted in ventricular irritability, serious cardiac arrhythmias, or death. The halogenated anesthetics sensitize the heart to the arrhythmogenic effects of catecholamines.', 'DDInter', 'This combination should be avoided or used very cautiously. If a halogenated anesthetic and epinephrine must be used together, an epinephrine concentration of 1:100,000 or 1:200,000 is recommended. The adult dosage should not exceed 100 mcg of a 1:100,000 solution in any 10-minute period. The patient''s hemodynamic status should be closely monitored. Lidocaine has a protective effect against the arrhythmogenic potential of epinephrine. Topical epinephrine appears to be relatively safe for use during anesthesia.', 'Others', 'This combination should be avoided or used very cautiously.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10034/', '', 'Tetracycline, Minocycline, Amphotericin B, Miconazole, Doxycycline, Clotrimazole, Prednisolone, Triamcinolone, Chlorhexidine, Neomycin, Formoterol, More', 'Thiopental, Alfentanil, Remifentanil, Sodium oxybate, Methohexital, Nitrous oxide, Esketamine', 1767369485);
