INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (10516, 'Alpelisib', 'Afatinib', 'Moderate', 'Coadministration of inhibitors of the breast cancer resistance protein (BCRP) transporter with alpelisib may increase the plasma concentrations and risk of adverse effects of alpelisib, which is a substrate of this efflux transporter. Alpelisib has been identified as a substrate of BCRP in in vitro studies. However, clinical data are not available.', 'DDInter', 'If coadministration with BCRP inhibitors is required, patients should be closely monitored for alpelisib-related adverse reactions, including hyperglycaemia, diarrhea, rash, acute kidney injury, abdominal pain, and anemia. Some authorities recommend coadministration of alpelisib with BCRP inhibitors be avoided whenever possible (US).', 'Others', 'If coadministration with BCRP inhibitors is required, patients should be closely monitored for alpelisib-related adverse reactions, including hyperglycaemia, diarrhea, rash, acute kidney injury, abdominal pain, and anemia.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10520/', '', 'Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Fedratinib, Avapritinib, Selumetinib, Vandetanib, Capmatinib, Duvelisib, Zanubrutinib, More', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Cobimetinib, Afatinib, More', 1767369485);
