INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (10819, 'Hyaluronidase', 'Ethinylestradiol', 'Minor', 'Larger amounts of hyaluronidase may be required to achieve equivalent dispersing effects if patients are concomitantly receiving large doses of salicylates, cortisone, ACTH, estrogens, or antihistamines. These drugs may render tissues partially resistant to the dispersing action of hyaluronidase.', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10823/', '', 'Ulipristal, Norethisterone, Norelgestromin, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Etonogestrel, More', 'Betibeglogene autotemcel, Streptokinase, Crizanlizumab, Lanadelumab, Icatibant', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (10820, 'Hydrocortisone', 'Ethinylestradiol', 'Moderate', 'Estrogens may enhance the systemic effects of both endogenous and exogenous corticosteroids. The proposed mechanism is an increase in serum cortisol-binding globulin (transcortin) induced by estrogens, resulting in a decreased rate of corticosteroid metabolic clearance. The interaction has been reported with estrogens or estrogen-containing oral contraceptives (OCs) and hydrocortisone, prednisone, and prednisolone.', 'DDInter', 'Patients treated concomitantly with an estrogen-containing drug may require lower dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever an estrogen is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Synergism', 'Patients treated concomitantly with an estrogen-containing drug may require lower dosages of corticosteroids or adrenocorticotropic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10824/', '', 'Fluorometholone, Indomethacin, Fluocinolone acetonide, Diclofenac, Epinephrine, Chlorhexidine, Amphotericin B, Acetylsalicylic acid, Salicylic acid, Ketorolac, Indomethacin, More', 'Ulipristal, Norethisterone, Norelgestromin, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Desogestrel, Dienogest, Etonogestrel, Norgestrel, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (10821, 'Imatinib', 'Ethinylestradiol', 'Moderate', 'Coadministration with imatinib may increase the plasma concentrations of drugs that are substrates of CYP450 2C9, 2D6 and/or 3A4. The mechanism is decreased clearance due to inhibition of these isoenzymes by imatinib.', 'DDInter', 'Caution is advised when imatinib is used concomitantly with drugs that undergo metabolism by CYP450 2C9, 2D6 and/or 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever imatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when imatinib is used concomitantly with drugs that undergo metabolism by CYP450 2C9, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10825/', '', 'Acalabrutinib, Dacomitinib, Avapritinib, Bosutinib, Ribociclib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Zanubrutinib, More', 'Norelgestromin, Megestrol acetate, Norgestimate, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Estetrol, Etonogestrel, Norgestrel, Diethylstilbestrol, More', 1767369485);
