INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (11200, 'Hydrocortisone', 'Fluconazole', 'Moderate', 'Coadministration with fluconazole may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4-mediated metabolism by fluconazole, a moderate inhibitor of the isoenzyme.', 'DDInter', 'Caution is advised when fluconazole is used with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fluconazole is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when fluconazole is used with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11204/', '', 'Tinidazole, Cefixime, Secnidazole, Micafungin, Miconazole, Oteseconazole, Flucytosine, Anidulafungin, Selenium Sulfide, Thiabendazole, Miconazole, More', 'Fluocinolone acetonide, Fluorometholone, Neomycin, Salicylic acid, Fluocinolone acetonide, Flurbiprofen, Fluorometholone, Bromfenac, Mometasone furoate, Amcinonide, Fluocinolone acetonide, More', 1767369485);
