INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (11282, 'Hydrocortisone', 'Levofloxacin', 'Major', 'Concomitant administration of corticosteroids may potentiate the risk of tendinitis and tendon rupture associated with fluoroquinolone treatment. The mechanism is unknown. Tendinitis and tendon rupture have most frequently involved the Achilles tendon, although cases involving the rotator cuff (the shoulder), the hand, the biceps, and the thumb have also been reported. Some have required surgical repair or resulted in prolonged disability. Tendon rupture can occur during or up to several months after completion of fluoroquinolone therapy.', 'DDInter', 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.g., age over 60 years; recipient of kidney, heart, and/or lung transplant). Patients should be advised to stop taking the fluoroquinolone, avoid exercise and use of the affected area, and promptly contact their physician if they experience pain, swelling, or inflammation of a tendon.', 'Synergism', 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11286/', '', 'Fluocinolone acetonide, Fluorometholone, Tetracycline, Minocycline, Amphotericin B, Miconazole, Doxycycline, Clotrimazole, Epinephrine, Chlorhexidine, Neomycin, More', 'Tinidazole, Dexlansoprazole, Misoprostol, Bismuth subcitrate potassium, Nizatidine, Esomeprazole, Vonoprazan, Tinidazole, Cefixime, Secnidazole, Rifamycin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (11283, 'Hydrocortisone', 'Licorice', 'Moderate', 'Licorice may potentiate the effects of corticosteroids. Licorice use has been associated with hypertension as well as sodium and water retention. Glycyrrhizic acid, a component of licorice, is hydrolyzed in the intestine to a metabolite (glycyrrhetinic acid) that causes mineralocorticoid and renin-suppressing effects. Prolonged use of licorice has led to a hypermineralocorticoid (pseudohyperaldosteronism) syndrome characterized by hypertension, hypernatremia, hypokalemia, metabolic alkalosis, renin-angiotensin-aldosterone suppression, and edema. In studies and case reports, licorice toxicity has generally been completely reversible within one to several weeks of licorice discontinuation. However, renin-angiotensin-aldosterone axis may be suppressed for up to several months.', 'DDInter', 'Patients receiving prolonged corticosteroid therapy should avoid or limit the consumption of licorice-containing products. Even relatively moderate doses of licorice may be problematic in susceptible patients when ingested regularly for prolonged periods.', 'Synergism', 'Patients receiving prolonged corticosteroid therapy should avoid or limit the consumption of licorice-containing products.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11287/', '', 'Fluorometholone, Indomethacin, Fluocinolone acetonide, Diclofenac, Tetracycline, Acetylsalicylic acid, Minocycline, Miconazole, Doxycycline, Metronidazole, Clotrimazole, More', '', 1767369485);
