INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (11301, 'Hydrocortisone', 'Meclofenamic acid', 'Moderate', 'The combined use of corticosteroids and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation.', 'DDInter', 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, H2-antagonists) may be considered.', 'Synergism', 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11305/', '', 'Fluocinolone acetonide, Fluorometholone, Tetracycline, Minocycline, Amphotericin B, Miconazole, Doxycycline, Metronidazole, Clotrimazole, Epinephrine, Chlorhexidine, More', 'Misoprostol, Chondroitin sulfate, Esomeprazole, Glucosamine, Tolazoline, Dimethyl sulfoxide', 1767369485);
