INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (11446, 'Hydrocortisone', 'Thalidomide', 'Major', 'Coadministration of thalidomide with glucocorticoids and/or antineoplastic agents in the treatment of malignancy may potentiate the risk of thromboembolism. The exact mechanism is unknown but likely multifactorial.', 'DDInter', 'Close monitoring for DVT or pulmonary embolism is recommended in patients who require thalidomide therapy in combination with glucocorticoids and/or cytotoxic agents. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain, shortness of breath, and pain or swelling in the arms or legs. Prophylaxis with anticoagulants such as low-molecular weight heparins or warfarin may be appropriate, but the decision to take thromboprophylactic measures should be made after careful assessment of underlying risk factors. If a thromboembolic event occurs during therapy with thalidomide, treatment must be discontinued and standard anticoagulation therapy started. Once anticoagulation is stabilized and complications of the thromboembolic event under control, thalidomide may be restarted at the original dose if benefit is deemed to outweigh the risks.', 'Synergism', 'Close monitoring for DVT or pulmonary embolism is recommended in patients who require thalidomide therapy in combination with glucocorticoids and/or cytotoxic agents.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11450/', '', 'Indomethacin, Fludrocortisone, Fluocinolone acetonide, Fluorometholone, Tetracycline, Minocycline, Miconazole, Epinephrine, Neomycin, Salicylic acid, Ketorolac, More', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (11447, 'Hydrocortisone', 'Theophylline', 'Moderate', 'The concomitant use of theophylline and corticosteroids may theoretically increase the risk of hypokalemia due to additive potassium-lowering effects. Additionally, theophylline serum concentrations may be altered.', 'DDInter', 'Monitoring for altered efficacy and safety of theophylline and altered serum potassium and theophylline concentrations is advisable when these drugs are coadministered. Patients should be advised to notify their physician if they experience signs of hypokalemia (e.g., weakness, lethargy, and muscle pains or cramps), worsening respiratory symptoms, or signs of theophylline toxicity (e.g., nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat).', 'Synergism', 'Monitoring for altered efficacy and safety of theophylline and altered serum potassium and theophylline concentrations is advisable when these drugs are coadministered.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11451/', '', 'Omalizumab, Tezepelumab, Benralizumab, Caffeine, Reslizumab, Dyphylline, Oxtriphylline, Mepolizumab, Zafirlukast, Bromotheophylline', 'Indomethacin, Fluocinolone acetonide, Fluorometholone, Clotrimazole, Chlorhexidine, Neomycin, Miconazole, Salicylic acid, Ketorolac, Indomethacin, Fluocinolone acetonide, More', 1767369485);
