INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (11692, 'Albendazole', 'Carbamazepine', 'Moderate', 'Coadministration with anticonvulsant drugs such as carbamazepine, phenytoin, phenobarbital, and primidone may decrease the plasma concentrations and half-life of the active metabolite of albendazole, albendazole sulfoxide. The mechanism is unknown, but induction of hepatic metabolism by these anticonvulsant drugs has been proposed.', 'DDInter', 'Caution and monitoring for altered clinical efficacy are recommended if albendazole is used concomitantly with anticonvulsant drugs such as carbamazepine, phenytoin, phenobarbital, or primidone in patients treated for systemic helminthic infections. Dose adjustments or alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'Caution and monitoring for altered clinical efficacy are recommended if albendazole is used concomitantly with anticonvulsant drugs such as carbamazepine, phenytoin, phenobarbital, or primidone in patients treated for systemic helminthic infections.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11696/', '', 'Thiabendazole, Levamisole, Pyrantel, Ivermectin, Piperazine', 'Brivaracetam, Topiramate, Ezogabine, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, More', 1767369485);
