INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (11800, 'Chlorpropamide', 'Albiglutide', 'Moderate', 'Coadministration of a glucagon-like peptide-1 (GLP-1) receptor agonist with an insulin secretagogue (e.g., sulfonylurea, meglitinide) may potentiate the risk of hypoglycemia. GLP-1 receptor agonists lower blood glucose by stimulating insulin secretion and lowering glucagon secretion.', 'DDInter', 'A lower dosage of the insulin secretagogue may be required when used in combination with a GLP-1 receptor agonist. Blood glucose should be monitored closely, and patients should be counseled to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypoglycemia occurs, patients should initiate appropriate remedial therapy immediately and contact their physician. Patients should also be advised to take precautions to avoid hypoglycemia while driving or operating hazardous machinery.', 'Synergism', 'A lower dosage of the insulin secretagogue may be required when used in combination with a GLP-1 receptor agonist.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11804/', '', 'Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Empagliflozin, Dulaglutide, Exenatide, Miglitol, Albiglutide, Rosiglitazone, Ertugliflozin, More', 'Glimepiride, Acetohexamide, Acarbose, Troglitazone, Chlorpropamide, Rosuvastatin, Tolazamide, Repaglinide, Tolbutamide', 1767369485);
