INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (11936, 'Albiglutide', 'Pasireotide', 'Moderate', 'Somatostatin analogs may alter the therapeutic response to insulin and other antidiabetic agents. Somatostatin analogs can induce hyperglycemia and, less frequently, hypoglycemia, by inhibiting the secretion of various counter-regulatory hormones involved in glucose homeostasis (i.e., glucagon, insulin, growth hormone, incretin hormones).', 'DDInter', 'Close monitoring of diabetic control is recommended if somatostatin analogs are prescribed to patients with preexisting diabetes. Glycemic status including fasting plasma glucose and/or hemoglobin A1c should be assessed prior to initiation of therapy and periodically during therapy in accordance with manufacturer''s product labeling, and the antidiabetic treatment adjusted as necessary.', 'Antagonism', 'Close monitoring of diabetic control is recommended if somatostatin analogs are prescribed to patients with preexisting diabetes.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11940/', '', 'Nafarelin, Elagolix, Cetrorelix, Ganirelix, Relugolix, Gonadorelin', 'Simvastatin, Guar gum, Rosuvastatin', 1767369485);
