INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (11938, 'Albiglutide', 'Pegvisomant', 'Moderate', 'Treatment with pegvisomant may increase glucose tolerance in some diabetic patients, potentially reducing their requirements for insulin and other antidiabetic agents. The mechanism is pegvisomant antagonism of endogenous growth hormone, the latter of which normally decreases insulin sensitivity and opposes the effects of insulin on carbohydrate metabolism. However, clinically relevant hypoglycemia was not observed in diabetic patients who were treated with pegvisomant in premarketing trials.', 'DDInter', 'Close clinical monitoring of glycemic control is recommended when pegvisomant is used in patients receiving insulin or other antidiabetic agents, and the dosages of these agents adjusted as necessary. Likewise, patients may be at risk for hyperglycemia when pegvisomant is withdrawn from their therapeutic regimen.', 'Synergism', 'Close clinical monitoring of glycemic control is recommended when pegvisomant is used in patients receiving insulin or other antidiabetic agents, and the dosages of these agents adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11942/', '', 'Mecasermin, Mecasermin rinfabate', 'Simvastatin, Guar gum, Rosuvastatin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (11939, 'Albiglutide', 'Pentamidine', 'Moderate', 'Pentamidine may interfere with the therapeutic effects of insulin and other antidiabetic agents. The use of pentamidine has been associated with disturbances in blood glucose homeostasis due to direct toxic effects on beta cells of the pancreas. Hypoglycemia, which may be severe and/or prolonged, as well as hyperglycemia and insulin-dependent diabetes mellitus, the latter of which may be irreversible, have been reported.', 'DDInter', 'Blood glucose should be monitored closely during and after pentamidine therapy in patients receiving insulin or other antidiabetic agents, especially if they are elderly or have renal impairment. Patients should learn to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypo- or hyperglycemia occur during pentamidine therapy, patients should initiate appropriate remedial therapy immediately and contact their physician. Dosage adjustments may be required if an interaction is suspected.', 'Antagonism', 'Blood glucose should be monitored closely during and after pentamidine therapy in patients receiving insulin or other antidiabetic agents, especially if they are elderly or have renal impairment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11943/', '', 'Miltefosine, Fexinidazole, Nifurtimox, Benznidazole', 'Simvastatin, Guar gum, Rosuvastatin', 1767369485);
