INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (12367, 'Venlafaxine', 'Salbutamol', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. The use of SNRIs alone has been associated with sustained increases in blood pressure and heart rate, and cases of elevated blood pressure requiring immediate treatment have been reported in postmarketing experience.', 'DDInter', 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter. Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12371/', '', 'St. John''s Wort, Oxitriptan, Viloxazine, Vortioxetine, Tryptophan, Vilazodone, Esketamine', 'Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Mometasone, Cromoglicic acid, Ipratropium, Beclomethasone dipropionate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (12368, 'Voriconazole', 'Salbutamol', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12372/', '', 'Flucytosine, Anidulafungin, Micafungin, Oteseconazole', 'Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Cromoglicic acid, Ipratropium, Epinephrine, Beclomethasone dipropionate, Ephedrine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (12369, 'Xylometazoline (nasal)', 'Salbutamol', 'Moderate', 'Coadministration of beta-2 adrenergic agonists with other adrenergic agents may potentiate the risk of cardiovascular side effects. Beta-2 adrenergic agonists can produce clinically significant cardiovascular effects including increases in pulse rate and systolic or diastolic blood pressure as well as ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression. The risk is lower when beta-2 adrenergic agonists are inhaled at normally recommended dosages.', 'DDInter', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension. Blood pressure and heart rate should be closely monitored.', 'Synergism', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12373/', '', 'Mometasone furoate, Umeclidinium, Budesonide, Glycopyrronium, Aclidinium, Mometasone, Epinephrine, Fluticasone, Cromoglicic acid, Ipratropium, Bitolterol, More', '', 1767369485);
