INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (12415, 'Ethanol', 'Alfuzosin', 'Moderate', 'The concurrent use of ethanol and alpha-1 adrenergic blockers may cause increased hypotensive effects. Patients with aldehyde dehydrogenase deficiencies (primarily Asians) may be at a higher risk of this interaction. The mechanism has not been determined. Data exist for prazosin and other alpha adrenergic blockers are expected to interact also. In addition, any patients taking alpha adrenergic blockers may experience excessive orthostatic hypotension with ethanol ingestion, due to ethanol''s unopposed vasodilatory effects in the presence of alpha adrenergic blockade.', 'DDInter', 'Patients who develop a flushing reaction after ethanol ingestion (indicates a possible aldehyde dehydrogenase deficiency) should be advised to avoid ethanol or limit their intake. All patients should be warned about the possibility of orthostatic hypotension with concurrent ethanol use.', 'Synergism', 'Patients who develop a flushing reaction after ethanol ingestion (indicates a possible aldehyde dehydrogenase deficiency) should be advised to avoid ethanol or limit their intake.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12419/', '', 'Finasteride, Dutasteride, Solifenacin', 'Hydroxocobalamin, Fomepizole, Ipecac, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Deferoxamine, Levoleucovorin, Methionine, Deferiprone, More', 1767369485);
