INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (12680, 'Ethanol', 'Metronidazole', 'Major', 'Use of alcohol or products containing alcohol during nitroimidazole therapy may result in a disulfiram-like reaction in some patients. There have been a few case reports involving metronidazole, although data overall are not convincing. The presumed mechanism is inhibition of aldehyde dehydrogenase (ALDH) by metronidazole in a manner similar to disulfiram. Following ingestion of alcohol, inhibition of ALDH results in increased concentrations of acetaldehyde, the accumulation of which can produce an unpleasant physiologic response referred to as the ''disulfiram reaction''. Symptoms include flushing, throbbing in head and neck, throbbing headache, respiratory difficulty, nausea, vomiting, sweating, thirst, chest pain, palpitation, dyspnea, hyperventilation, tachycardia, hypotension, syncope, weakness, vertigo, blurred vision, and confusion. Severe reactions may result in respiratory depression, cardiovascular collapse, arrhythmia, myocardial infarction, acute congestive heart failure, unconsciousness, convulsions, and death.', 'DDInter', 'Because clear evidence is lacking concerning the safety of ethanol use during nitroimidazole therapy, patients should be apprised of the potential for interaction. Consumption of alcoholic beverages and products containing propylene glycol is specifically contraindicated during and for at least 3 days after completion of metronidazole and benznidazole therapy according to their product labeling.', 'Metabolism', 'Because clear evidence is lacking concerning the safety of ethanol use during nitroimidazole therapy, patients should be apprised of the potential for interaction.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12684/', '', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Pantoprazole, Levofloxacin, Tetracycline, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Famotidine, Rifabutin, Nizatidine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (12681, 'Ethanol', 'Metronidazole (topical)', 'Moderate', 'Use of alcohol or products containing alcohol during nitroimidazole therapy may result in a disulfiram-like reaction in some patients. There have been a few case reports involving metronidazole, although data overall are not convincing. The presumed mechanism is inhibition of aldehyde dehydrogenase (ALDH) by metronidazole in a manner similar to disulfiram. Following ingestion of alcohol, inhibition of ALDH results in increased concentrations of acetaldehyde, the accumulation of which can produce an unpleasant physiologic response referred to as the ''disulfiram reaction''. Symptoms include flushing, throbbing in head and neck, throbbing headache, respiratory difficulty, nausea, vomiting, sweating, thirst, chest pain, palpitation, dyspnea, hyperventilation, tachycardia, hypotension, syncope, weakness, vertigo, blurred vision, and confusion. Severe reactions may result in respiratory depression, cardiovascular collapse, arrhythmia, myocardial infarction, acute congestive heart failure, unconsciousness, convulsions, and death.', 'DDInter', 'Because clear evidence is lacking concerning the safety of ethanol use during nitroimidazole therapy, patients should be apprised of the potential for interaction and instructed to avoid alcoholic beverages and products containing alcohol or propylene glycol while using oral, intravenous, or vaginal preparations of a nitroimidazole. Alcoholic beverages should not be consumed for up to 3 days after completion of systemic nitroimidazole therapy.', 'Metabolism', 'Because clear evidence is lacking concerning the safety of ethanol use during nitroimidazole therapy, patients should be apprised of the potential for interaction and instructed to avoid alcoholic beverages and products containing alcohol or propylene glycol while using oral, intravenous, or vaginal preparations of a nitroimidazole.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12685/', '', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Promethazine, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Pantoprazole, Levofloxacin, Tetracycline, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Famotidine, Rifabutin, Nizatidine, More', 1767369485);
