INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (12701, 'Ethanol', 'Nifurtimox', 'Major', 'Use of alcohol or products containing alcohol during therapy with nitrofurans may result in a disulfiram-like reaction in some patients. The presumed mechanism is inhibition of aldehyde dehydrogenase (ALDH) by nitrofurans in a manner similar to disulfiram. Following ingestion of alcohol, inhibition of ALDH results in increased concentrations of acetaldehyde, the accumulation of which can produce an unpleasant physiologic response referred to as the ''disulfiram reaction''. Symptoms include flushing, throbbing in head and neck, throbbing headache, respiratory difficulty, nausea, vomiting, sweating, thirst, chest pain, palpitation, dyspnea, hyperventilation, tachycardia, hypotension, syncope, weakness, vertigo, blurred vision, and confusion. Severe reactions may result in respiratory depression, cardiovascular collapse, arrhythmia, myocardial infarction, acute congestive heart failure, unconsciousness, convulsions, and death.', 'DDInter', 'Consumption of alcohol is considered contraindicated during nifurtimox therapy.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12705/', '', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Promethazine, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Pentamidine, Miltefosine', 1767369485);
