INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (12749, 'Ethanol', 'Pravastatin', 'Moderate', 'Concomitant use of statin medication with substantial quantities of alcohol may increase the risk of hepatic injury. Transient increases in serum transaminases have been reported with statin use and while these increases generally resolve or improve with continued therapy or a brief interruption in therapy, there have been rare postmarketing reports of fatal and non-fatal hepatic failure in patients taking statins. Patients who consume substantial quantities of alcohol and/or have a history of liver disease may be at increased risk for hepatic injury. Active liver disease or unexplained transaminase elevations are contraindications to statin use.', 'DDInter', 'Patients should be counseled to avoid substantial quantities of alcohol in combination with statin medications and clinicians should be aware of the increased risk for hepatotoxicity in these patients.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12753/', '', 'Gemfibrozil, Fenofibric acid, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Ezetimibe, Clofibrate, Colestipol, Fenofibrate, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Deferoxamine, Levoleucovorin, Arginine, More', 1767369485);
