INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (13498, 'Hydrocodone', 'Alefacept', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', 'DDInter', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/13502/', '', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Siponimod, Apremilast, More', 'Dextromethorphan, Clofedanol, Benzonatate, Pentoxyverine, Codeine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (13499, 'Hydroxyurea', 'Alefacept', 'Moderate', 'The use of alefacept with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 'Patients receiving other immunosuppressive or myelosuppressive agents or phototherapy should not be treated with alefacept because of the possibility of excessive immunosuppression.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/13503/', '', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Tacrolimus, Abatacept, Apremilast, Inebilizumab, Belumosudil, More', 'Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Pegaspargase, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Anagrelide, More', 1767369485);
