INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14017, 'Iron sucrose', 'Alendronic acid', 'Moderate', 'INTERVAL: Coadministration with sucroferric oxyhydroxide may reduce the oral bioavailability of bisphosphonates. The proposed mechanism is chelation of bisphosphonates to the ferric ion component of sucroferric oxyhydroxide, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract.', 'DDInter', 'Alendronate should be administered at least 1 hour before sucroferric oxyhydroxide. No dosing recommendations are available for use with other bisphosphonates. Patients may follow the manufacturers'' suggestion provided in the individual bisphosphonate label regarding use with mineral supplements and other polyvalent cation-containing products (i.e., administer sucroferric oxyhydroxide at least 30 minutes after risedronate, 1 hour after ibandronate, and at least 2 hours before or 2 hours after clodronate, etidronate, or tiludronate).', 'Absorption', 'Alendronate should be administered at least 1 hour before sucroferric oxyhydroxide.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14021/', '', 'Vosoritide, Denosumab, Burosumab, Romosozumab, Zoledronic acid, Pamidronic acid', 'Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Difelikefalin, Cobicistat, Levoleucovorin, Arginine, Methionine, Deferiprone, More', 1767369485);
