INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14100, 'Alfentanil', 'Atazanavir', 'Major', 'Coadministration with potent and moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of alfentanil, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Lower dosages of alfentanil may be required when used in combination with potent and moderate CYP450 3A4 inhibitors (e.g., azole antifungal agents, protease inhibitors, ketolide and certain macrolide antibiotics, aprepitant, diltiazem, dalfopristin-quinupristin, delavirdine, imatinib, nefazodone, verapamil). Patients should be carefully monitored for excessive central nervous system and respiratory depression, and dosage adjustments made accordingly if necessary. Recovery time from alfentanil anesthesia may be prolonged in some cases.', 'Metabolism', 'Lower dosages of alfentanil may be required when used in combination with potent and moderate CYP450 3A4 inhibitors (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14114/', '', 'Halothane, Thiopental, Remifentanil, Sodium oxybate, Isoflurane, Methohexital, Nitrous oxide, Desflurane, Propofol, Enflurane, Esketamine, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 1767369485);
