INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14772, 'Fosamprenavir', 'Brigatinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of brigatinib, which is partially metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of brigatinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the brigatinib once daily dose should be reduced by approximately 50% (i.e., from 180 mg to 90 mg, or from 90 mg to 60 mg). Patients should be closely monitored for increased adverse reactions such as nausea, vomiting, diarrhea, hypertension, bradycardia, hyperglycemia, visual disturbances, lymphopenia, anemia, and elevations in pancreatic enzymes and creatine phosphokinase. Following discontinuation of the CYP450 3A4 inhibitor, the brigatinib dose that was tolerated previously may be resumed.', 'Metabolism', 'Concomitant use of brigatinib with potent CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14786/', '', 'Lamivudine, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Futibatinib, Imatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib, Binimetinib', 1767369485);
