INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14783, 'Fosamprenavir', 'Capmatinib', 'Major', 'Coadministration of capmatinib with strong CYP450 3A4 inhibitors may increase the risk and severity of capmatinib adverse effects, such as interstitial lung disease, pneumonitis, and hepatotoxicity. The proposed mechanism is decreased clearance due to inhibition of CYP450 3A4, which is one of the primary enzymes responsible for the metabolic clearance of capmatinib.', 'DDInter', 'Close monitoring is recommended whenever capmatinib is used with a strong CYP450 3A4 inhibitor. Clinical and laboratory monitoring should be considered whenever a strong CYP450 3A4 inhibitor is added to or withdrawn from therapy with capmatinib, and the dosage adjusted as necessary based on clinical response and toxicity. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Close monitoring is recommended whenever capmatinib is used with a strong CYP450 3A4 inhibitor.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14797/', '', 'Lamivudine, Glecaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, Acyclovir, More', 'Futibatinib, Imatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib, Binimetinib', 1767369485);
