INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14836, 'Fosamprenavir', 'Efavirenz', 'Moderate', 'Coadministration with efavirenz may decrease the plasma concentrations of amprenavir. The mechanism is efavirenz induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of amprenavir.', 'DDInter', 'The dosage of amprenavir or its prodrug, fosamprenavir, should probably be increased when coadministered with efavirenz in the absence of ritonavir as a pharmacokinetic booster, although appropriate dosages with respect to safety and efficacy have not been established. Fosamprenavir labeling recommends an additional 100 mg/day (300 mg total) of ritonavir when efavirenz is administered with the fosamprenavir-ritonavir once-daily regimen based on pharmacokinetic studies. No change in the ritonavir dose is required when efavirenz is administered with the fosamprenavir-ritonavir twice-daily regimen.', 'Metabolism', 'The dosage of amprenavir or its prodrug, fosamprenavir, should probably be increased when coadministered with efavirenz in the absence of ritonavir as a pharmacokinetic booster, although appropriate dosages with respect to safety and efficacy have not been established.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14850/', '', 'Glecaprevir, Cabotegravir, Amprenavir, Zanamivir, Lenacapavir, Velpatasvir, Rilpivirine, Oseltamivir, Brincidofovir, Remdesivir, Famciclovir, More', 'Paritaprevir, Cabotegravir, Zanamivir, Oseltamivir, Brincidofovir, Elbasvir, Voxilaprevir, Pibrentasvir, Rimantadine, Telbivudine, Efavirenz, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14837, 'Fosamprenavir', 'Elagolix', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14851/', '', 'Lamivudine, Paritaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, More', 'Nafarelin, Octreotide, Cetrorelix, Pasireotide, Estradiol, Ganirelix, Relugolix, Lanreotide, Gonadorelin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14838, 'Fosamprenavir', 'Eletriptan', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eletriptan, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in increased risk of vasospastic reactions associated with the use of 5-HT1 receptor agonists, such as coronary artery vasospasm, peripheral vascular ischemia, and colonic ischemia.', 'DDInter', 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors. Alternatively, other 5-HT1 receptor agonists that are not metabolized by CYP450 3A4 may be considered, such as frovatriptan, naratriptan, rizatriptan, sumatriptan, and zolmitriptan.', 'Metabolism', 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14852/', '', 'Frovatriptan, Eptinezumab, Lasmiditan, Rizatriptan, Naratriptan, Galcanezumab, Zolmitriptan, Sumatriptan, Clonidine, Erenumab, Fremanezumab', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14839, 'Fosamprenavir', 'Eliglustat', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 'The use of eliglustat with a potent CYP450 3A4 inhibitor is considered contraindicated in CYP450 2D6 poor metabolizers (PMs) and intermediate metabolizers (IMs). The contraindication is extended to extensive metabolizers (EMs) when a moderate or potent CYP450 2D6 inhibitor is given concomitantly with a moderate or potent CYP450 3A4 inhibitor. In the absence of a concomitant CYP450 2D6 inhibitor, eliglustat may be prescribed at a reduced dosage of 84 mg once daily to EMs treated with a potent CYP450 3A4 inhibitor. Potent CYP450 3A4 inhibitors include itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, ceritinib, idelalisib, mibefradil, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. Potent and moderate CYP450 2D6 inhibitors include abiraterone, bupropion, celecoxib, cimetidine, cinacalcet, clobazam, darifenacin, diphenhydramine, dronedarone, duloxetine, flecainide, fluoxetine, methotrimeprazine, mirabegron, paroxetine, propafenone, propoxyphene, quinidine, ranolazine, sertraline, and terbinafine. Some drugs such as cobicistat, delavirdine, mibefradil, and ritonavir are dual CYP450 3A4 and 2D6 inhibitors, and they should not be used with eliglustat in any patient regardless of their CYP450 2D6 metabolizer status.', 'Metabolism', 'The use of eliglustat with a potent CYP450 3A4 inhibitor is considered contraindicated in CYP450 2D6 poor metabolizers (PMs) and intermediate metabolizers (IMs).', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14853/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14840, 'Fosamprenavir', 'Eluxadoline', 'Moderate', 'It is not known whether, and to what extent, eluxadoline may be metabolized by CYP450 isoenzymes and may interact with inhibitors of CYP450 isoenzymes.', 'DDInter', 'As a precautionary measure due to incomplete information on the metabolism of eluxadoline, the manufacturer recommends caution during coadministration with potent inhibitors of various CYP450 isoenzymes. Patients should be monitored for adverse effects such as sedation, nausea, vomiting, constipation, abdominal pain, liver enzyme elevations, and pancreatitis,', 'Metabolism', 'As a precautionary measure due to incomplete information on the metabolism of eluxadoline, the manufacturer recommends caution during coadministration with potent inhibitors of various CYP450 isoenzymes.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14854/', '', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, Rilpivirine, Etravirine, More', 'Opium, Diphenoxylate, Difenoxin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14841, 'Fosamprenavir', 'Empagliflozin', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14855/', '', 'Lamivudine, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Rilpivirine, Etravirine, Dolutegravir, More', 'Guar gum, Rosuvastatin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14842, 'Fosamprenavir', 'Encorafenib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of encorafenib, which is primarily metabolized by the isoenzyme. Increased exposure to encorafenib may increase the risk of serious and life-threatening adverse effects such as hemorrhage, uveitis, QT prolongation, hepatotoxicity, dermatologic reactions, and new malignancies.', 'DDInter', 'Concomitant use of encorafenib with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing the encorafenib dose to one-half of the dose used prior to addition of the moderate CYP450 3A4 inhibitor. After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the encorafenib dose that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', 'Concomitant use of encorafenib with moderate CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14856/', '', 'Lamivudine, Glecaprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Stavudine, Remdesivir, More', 'Futibatinib, Imatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib, Binimetinib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14843, 'Fosamprenavir', 'Enfortumab vedotin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of enfortumab vedotin. Enfortumab vedotin is an antibody-drug conjugate (ADC) that releases MMAE via proteolytic cleavage, and MMAE has been shown in vitro to be primarily metabolized by CYP450 3A4.', 'DDInter', 'Caution is advised when enfortumab vedotin is used concomitantly with potent CYP450 3A4 inhibitors. Patients should be closely monitored for development or exacerbation of toxicities such as peripheral neuropathy, hyperglycemia, dermatologic reactions (maculopapular rash, pruritus, symmetrical drug-related intertriginous and flexural exanthema (SDRIFE), bullous dermatitis, exfoliative dermatitis, palmar-plantar erythrodysesthesia), and ocular disorders (dry eyes, keratitis, blurred vision, limbal stem cell deficiency), and the dosing of enfortumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when enfortumab vedotin is used concomitantly with potent CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14857/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14844, 'Fosamprenavir', 'Loperamide', 'Major', 'Coadministration with drugs that enhance the gastrointestinal absorption or inhibit the metabolism of loperamide (e.g., potent CYP450 3A4 or 2C8 inhibitors) may increase the plasma concentrations and adverse effects of loperamide.', 'DDInter', 'Caution is recommended if loperamide is used with drugs that enhance its gastrointestinal absorption or inhibit its metabolism. Particular caution is advised when drugs that inhibit CYP450 3A4 (e.g., azole antifungal agents, clarithromycin, cobicistat, conivaptan, delavirdine, erythromycin, idelalisib, nefazodone, protease inhibitors, telithromycin) and CYP450 2C8 (e.g., gemfibrozil, clopidogrel) are used together with loperamide, or when one or more of these drugs are combined with inhibitors of P-glycoprotein transport (e.g., amiodarone, cyclosporine, diltiazem, dronedarone, quinidine, verapamil), since they may act synergistically to increase loperamide concentrations. Patients should be counseled to not exceed the recommended dosage and frequency or duration of use of loperamide, and to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If loperamide-induced cardiotoxicity is suspected, promptly discontinue loperamide and initiate therapy to manage and prevent cardiac arrhythmias and adverse outcomes. Electrical pacing or cardioversion may be necessary if torsade de pointes persists despite pharmacotherapy.', 'Metabolism', 'Caution is recommended if loperamide is used with drugs that enhance its gastrointestinal absorption or inhibit its metabolism.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14858/', '', 'Opium, Diphenoxylate, Difenoxin', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Etravirine, Dolutegravir, Oseltamivir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14845, 'Fosamprenavir', 'Methadone', 'Moderate', 'Methadone may decrease the plasma concentrations of amprenavir in the absence of low-dose ritonavir as a pharmacokinetic booster. Coadministration with fosamprenavir alone or in combination with low-dose ritonavir may decrease the plasma concentrations of methadone. The exact mechanism of interaction has not been described.', 'DDInter', 'Based on available pharmacokinetic data, the interaction between methadone and fosamprenavir/ritonavir is not expected to be clinically significant. However, as a precaution, patients should be monitored for opiate withdrawal symptoms if prescribed fosamprenavir/ritonavir while receiving methadone treatment. Patients should be advised to notify their caregiver if they experience symptoms of methadone withdrawal such as restlessness, insomnia, sweating, lacrimation, or rhinorrhea. Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, fosamprenavir should preferably not be used with methadone in the absence of ritonavir as a pharmacokinetic booster.', 'Others', 'Based on available pharmacokinetic data, the interaction between methadone and fosamprenavir/ritonavir is not expected to be clinically significant.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14859/', '', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Codeine, Naltrexone, Oxymorphone, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14846, 'Fosaprepitant', 'Alfuzosin', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of alfuzosin, which is primarily metabolized by the isoenzyme. Hypotension, syncope, QT prolongation, and priapism may occur.', 'DDInter', 'Caution is advised if alfuzosin is prescribed in combination with moderate CYP450 3A4 inhibitors. Pharmacologic response to alfuzosin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the alfuzosin dosage adjusted as necessary. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, orthostasis, syncope, or tachycardia. Taking alfuzosin at bedtime may minimize the occurrence of orthostatic effects. Patients should also avoid driving or operating hazardous machinery until they know how the medication affects them.', 'Metabolism', 'Caution is advised if alfuzosin is prescribed in combination with moderate CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14860/', '', 'Finasteride, Terazosin', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14847, 'Fosaprepitant', 'Aliskiren', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and P-glycoprotein may increase the plasma concentrations and pharmacologic effects of aliskiren, which is a substrate of both the isoenzyme and efflux transporter. According to the product labeling, plasma levels of aliskiren were increased approximately 80% by the potent CYP450 3A4 and moderate P-gp inhibitor ketoconazole at a dosage of 200 mg twice daily.', 'DDInter', 'Pharmacologic response to aliskiren should be monitored more closely whenever a CYP450 3A4 and/or P-gp inhibitor is added to or withdrawn from therapy, and the aliskiren dosage adjusted if necessary. Patients should be advised to notify their physician if they experience excessive adverse effects of aliskiren such as dizziness, lightheadedness, diarrhea, abdominal pain, and gastroesophageal reflux.', 'Metabolism', 'Pharmacologic response to aliskiren should be monitored more closely whenever a CYP450 3A4 and/or P-gp inhibitor is added to or withdrawn from therapy, and the aliskiren dosage adjusted if necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14861/', '', 'Hydrochlorothiazide, Valsartan, Hydrochlorothiazide, Olmesartan, Azilsartan medoxomil, Nebivolol, Telmisartan, Candesartan, Sacubitril, Eprosartan', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14848, 'Fosaprepitant', 'Almotriptan', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', 'DDInter', 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever aprepitant or fosaprepitant is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14862/', '', 'Frovatriptan, Eptinezumab, Lasmiditan, Rizatriptan, Naratriptan, Galcanezumab, Zolmitriptan, Sumatriptan, Clonidine, Erenumab, Fremanezumab', '', 1767369485);
