INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (14973, 'Foscarnet', 'Clofazimine', 'Moderate', 'Clofazimine may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if clofazimine is used in combination with other drugs that can prolong the QT interval. Consider monitoring electrocardiograms, particularly in patients receiving clofazimine dosages greater than 100 mg/day, and discontinue QT-prolonging medications if clinically significant ventricular arrhythmia is noted or if QTcF interval >=500 ms is observed. If syncope occurs, obtain an ECG to detect QT prolongation.', 'Synergism', 'Caution is recommended if clofazimine is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14987/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Dapsone', 1767369485);
