INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (15228, 'Fosphenytoin', 'Emapalumab', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', 'DDInter', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15243/', '', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, Ethotoin, Rufinamide, More', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Abatacept, Inebilizumab, Cladribine, Ozanimod, Belimumab, Sutimlimab, Risankizumab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (15229, 'Fosphenytoin', 'Empagliflozin', 'Moderate', 'Coadministration of empagliflozin with inducers of uridine diphosphate glucuronosyl transferase (UGT) enzymes may decrease the plasma concentrations and efficacy of empagliflozin, which is primarily metabolized in vitro by UGT1A3, 1A8, 1A9, and 2B7.', 'DDInter', 'Coadministration of empagliflozin with UGT inducers should be avoided if possible. If concomitant use is required, dosage adjustments as well as increased clinical and laboratory monitoring should be considered whenever a UGT inducer is added to or withdrawn from empagliflozin therapy.', 'Metabolism', 'Coadministration of empagliflozin with UGT inducers should be avoided if possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15244/', '', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, More', 'Dapagliflozin, Guar gum, Rosuvastatin', 1767369485);
