INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (15498, 'Furosemide', 'Cholestyramine', 'Moderate', 'INTERVAL: Limited data have shown that the bioavailability of orally administered furosemide is reduced by 95% during concurrent administration of cholestyramine and by 80% during concurrent administration of colestipol. (These figures are based on data from the area under the plasma concentration-time curve and total urinary elimination data.) These decreases in bioavailability were associated with 77% and 58% reductions in the diuretic response to furosemide. No data are available for bumetanide, torsemide, or ethacrynic acid, but a similar interaction is suspected.', 'DDInter', 'Diuretics should be administered at least 2 to 3 hours before cholestyramine or colestipol. If alternating dosage schedules is ineffective, it may be necessary to increase diuretic dosage during coadministration. Patients should be monitored for altered diuretic efficacy.', 'Absorption', 'Diuretics should be administered at least 2 to 3 hours before cholestyramine or colestipol.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15513/', '', 'Tamsulosin, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, Ciclopirox, More', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Lomitapide, Bempedoic acid, Colesevelam, Alirocumab', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (15499, 'Furosemide', 'Ciprofloxacin', 'Minor', 'The plasma concentration of lomefloxacin following administration of furosemide has been reported to be higher than after lomefloxacin alone. The area under the curve of lomefloxacin was increased and the total and renal clearances of lomefloxacin were decreased. A similar interaction may occur if other quinolones are administered with other loop diuretics. The loop diuretic may decrease the renal clearance of the quinolone by competing for tubular secretion.', 'DDInter', '-', 'Excretion', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15514/', '', 'Tinidazole, Levofloxacin, Secnidazole, Levofloxacin, Nalidixic acid, Gemifloxacin, Grepafloxacin, Cinoxacin, Trovafloxacin, Delafloxacin, Sparfloxacin, More', 'Trichlormethiazide, Bendroflumethiazide, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Methazolamide, Trichlormethiazide, More', 1767369485);
