INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (15704, 'Gallium nitrate', 'Balsalazide', 'Moderate', 'Coadministration of 5-aminosalicylate with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of 5-aminosalicylate or its prodrugs has been associated with rare reports of renal impairment including minimal change nephropathy, acute and chronic interstitial nephritis, and renal failure.', 'DDInter', 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during 5-aminosalicylate therapy.', 'Synergism', 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15719/', '', 'Budesonide, Hydrocortisone, Cromoglicic acid, Prednisone, Prednisolone, Betamethasone, Beclomethasone dipropionate', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (15705, 'Human botulinum neurotoxin A/B immune globulin', 'Gallium nitrate', 'Major', 'Coadministration of intravenous immune globulin preparations with nephrotoxic agents may potentiate the risk of renal impairment. Many commercially available intravenous formulations of immune globulin contain sucrose as a stabilizer.', 'DDInter', 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.g., e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Clinicians should ensure that patients are not volume depleted prior to the initiation of immune globulin therapy. Monitoring of urine output and renal function tests, including the measurement of blood urea nitrogen (BUN) and serum creatinine, is recommended prior to the initial infusion and at appropriate intervals thereafter.', 'Synergism', 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15720/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (15706, 'Gallium nitrate', 'Cidofovir', 'Major', 'Coadministration of cidofovir with other nephrotoxic agents may increase the risk of renal impairment due to additive effects on the kidney. Dose-related nephrotoxicity is the major toxicity of cidofovir. Cases of acute renal failure resulting in dialysis and/or contributing to death have occurred with as few as one or two doses of cidofovir.', 'DDInter', 'Concomitant use of cidofovir with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; aminosalicylates; antiviral/antiretroviral agents such as acyclovir, adefovir, foscarnet, ganciclovir, and tenofovir; antineoplastics such as aldesleukin, cisplatin, clofarabine, ifosfamide, streptozocin, and high intravenous dosages of methotrexate; chelating agents such as deferasirox, deferoxamine, edetate disodium, and edetate calcium disodium; immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus; intravascular contrast media; intravenous bisphosphonates; intravenous pentamidine; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents; gallium nitrate; lithium; penicillamine) is considered contraindicated.', 'Synergism', 'Concomitant use of cidofovir with other potentially nephrotoxic agents (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15721/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (15707, 'Gallium nitrate', 'Clofarabine', 'Moderate', 'Coadministration of clofarabine with other nephrotoxic agents may increase the risk of renal impairment due to additive effects on the kidney. Moreover, renal impairment secondary to the use of these agents may reduce the clearance of clofarabine, which is primarily eliminated by renal excretion. This may increase the risk of other adverse effects such as nausea, vomiting, stomatitis, hypertension, hypotension, myelosuppression, hemorrhage, and hepatotoxicity.', 'DDInter', 'Drugs that are potentially nephrotoxic (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; aminosalicylates; antiviral/antiretroviral agents such as acyclovir, adefovir, cidofovir, foscarnet, ganciclovir, and tenofovir; antineoplastics such as aldesleukin, carboplatin, cisplatin, ifosfamide, streptozocin, and high intravenous dosages of methotrexate; chelating agents such as deferasirox, deferoxamine, edetate disodium, and edetate calcium disodium; immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus; intravenous bisphosphonates; intravenous pentamidine; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents; gallium nitrate; lithium; penicillamine) should be avoided during the 5 days of clofarabine administration if possible. Renal function should be evaluated prior to and during therapy, and administration discontinued immediately if substantial increases (e.g., grade 3 or higher) in creatinine are noted.', 'Excretion', 'Drugs that are potentially nephrotoxic (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15722/', '', 'Methotrexate, Fluorouracil, Mercaptopurine, Cladribine, Trifluridine, Floxuridine, Gemcitabine, Nelarabine, Cytarabine, Pralatrexate, Fludarabine, More', '', 1767369485);
