INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (15756, 'Ganciclovir', 'Didanosine', 'Moderate', 'The coadministration of didanosine and ganciclovir may result in significantly elevated plasma concentrations of didanosine, while the pharmacokinetics of ganciclovir are minimally affected. Other studies have reported similar or even greater alterations with both intravenous and oral ganciclovir. The mechanism is unknown but may involve competitive inhibition of didanosine renal tubular secretion or increased bioavailability of didanosine by ganciclovir. Caution is advised if didanosine must be used concomitantly with ganciclovir or its prodrug, valganciclovir.', 'DDInter', 'Close monitoring for toxicities associated with didanosine such as pancreatitis, lactic acidosis, and peripheral neuropathy is recommended during concurrent therapy. Patients should be advised to promptly notify their physician if they experience possible symptoms of toxicity (e.g., abdominal pain, nausea, vomiting, diarrhea, weakness, dizziness, lightheadedness, irregular heartbeat, or numbness, pain, or tingling in the hands and feet).', 'Excretion', 'Close monitoring for toxicities associated with didanosine such as pancreatitis, lactic acidosis, and peripheral neuropathy is recommended during concurrent therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15771/', '', 'Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, More', 'Paritaprevir, Glecaprevir, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, Oseltamivir, More', 1767369485);
