INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (15801, 'Gefitinib', 'Deferasirox', 'Moderate', 'Coadministration with deferasirox may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism may involve induction of CYP450 3A4 activity by deferasirox, although in vitro, the drug has been shown to inhibit CYP450 3A4.', 'DDInter', 'Caution is advised if deferasirox must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever deferasirox is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if deferasirox must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15816/', '', 'Futibatinib, Nilotinib, Lenvatinib, Pralsetinib, Dabrafenib, Asciminib, Vandetanib, Neratinib, Afatinib, Erdafitinib, Trametinib, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Deferoxamine, Levoleucovorin, Arginine, More', 1767369485);
