INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (16740, 'Aliskiren', 'Ardeparin', 'Moderate', 'The concomitant use of aliskiren and heparin or low molecular weight heparins may increase the risk of hyperkalemia. Both aliskiren and some heparins individually have been associated with increased potassium levels or hyperkalemia. Patients with renal impairment, diabetes, old age, and severe or worsening heart failure may be at a greater risk.', 'DDInter', 'Caution is advised if aliskiren is used with heparin or low molecular weight heparin. It may be advisable to monitor serum potassium and renal function.', 'Synergism', 'Caution is advised if aliskiren is used with heparin or low molecular weight heparin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16755/', '', 'Amlodipine, Hydrochlorothiazide, Amlodipine, Valsartan, Irbesartan, Hydrochlorothiazide, Losartan, Olmesartan, Azilsartan medoxomil, Nebivolol, Telmisartan, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (16741, 'Aliskiren', 'Acetylsalicylic acid', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of aliskiren. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Clinical data are limited. Concomitant use of NSAIDs and aliskiren may cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function. Acute renal failure may occur, although effects are usually reversible.', 'DDInter', 'Patients receiving aliskiren who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Patients receiving aliskiren who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16756/', '', 'Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, More', 'Sacubitril', 1767369485);
